rva = "Vehicular";      	      	if($esta!=0)      	{      		if($idc!=null)      		{      			$yaAlV = controlVisita::getVisitaWithId($idvP);      			if($yaAlV!=null)      			{      				$idVisitanew = $yaAlV->getidVisista();      			}      			else      			{      				$idVisitanew = controlVisita::insertVisitaYretId($dataVisita,$idc);      			}	      			if($idVisitanew!=null && $idVisitanew>0)      			{      				$dataReserva = array($idc,null,$idVisitanew,$st,$et,$tipoReserva,0,$pisos,$oficina,$esta,"Reservada",null,$idVisitanew,$patente);      				controlReserva::insertaReserva($dataReserva);      			}      			       		}			       	}      }      else       {      	if($idc!=null)      	{      		$yaAlV = controlVisita::getVisitaWithId($idvP);      		//echo var_dump($dataVisita)." ".$idc.'<br>'.$yaAlV."         ";      			if($yaAlV!=null)      			{      				$idVisitanew = $yaAlV->getidVisista();      			}      			else      			{      				$idVisitanew = controlVisita::insertVisitaYretId($dataVisita,$idc);      			}	      			      		if($idVisitanew!=null && $idVisitanew>0)      		{      			$dataReserva = array($idc,null,$idVisitanew,$st,$et,$tipoReserva,0,$pisos,$oficina,$esta,"Reservada",null,$idVisitanew,0);      			controlReserva::insertaReserva($dataReserva);      		}      	      	}      	      }                        $ret['Msg'] = 'add success';      $ret['Data'] = rand();  return $ret;}function listCalendarByRange($sd, $ed, $cnt){  $ret = array();  $ret['events'] = array();  $ret["issort"] =true;  $ret["start"] = php2JsTime($sd);  $ret["end"] = php2JsTime($ed);  $ret['error'] = null;  $title = array('team meeting', 'remote meeting', 'project plan review', 'annual report', 'go to dinner');  $location = array('Lodan', 'Newswer', 'Belion', 'Moore', 'Bytelin');  for($i=0; $i<$cnt; $i++) {      $rsd = rand($sd, $ed);      $red = rand(3600, 10800);      if(rand(0,10) > 8){          $alld = 1;      }else{          $alld=0;      }      $ret['events'][] = array(        rand(10000, 22222),        $title[rand(0,4)],        php2JsTime($rsd),        php2JsTime($red),        rand(0,1),        $alld, //more than one day event        0,//Recurring event        rand(-1,13),        1, //editable        $location[rand(0,4)],         ''//$attends      );  }  return $ret;}function listCalendarByRangeTst($reser,$et,$ed){
	$ret = array();
	$ret['events'] = array();
	$ret["issort"] =true;
	$ret["start"] = php2JsTime($et);
	$ret["end"] = php2JsTime($ed);
	$ret['error'] = null;	$rsd = $et;	$visita = null;	if($reser!=null && count($reser)>0)	{			foreach ($reser as $reserva)			{				$visita = controlVisita::getVisitaWithId($reserva->getidVisita());				//print_r($visita);				$tipoReserva = $reserva->gettipoReserva();				$color = 1;				$estac = "";				if($tipoReserva=="Vehicular")				{					$color = 5;					$estac = " estacionamiento: ".$reserva->getestacionamientoAsignado();				}								$fechaE = new DateTime($reserva->getfechaEntrada());				$fechaS = new DateTime($reserva->getfechaSalida());
				//echo $fecha->format('d/m/y H:i')				$phpTimeI = js2PhpTime($fechaE->format('m/d/y H:i'));				$phpTimeS = js2PhpTime($fechaS->format('m/d/y H:i'));				//echo "nuestra ".php2JsTime($phpTimeI)."   ";				//echo "siste".php2JsTime($rsd);				$ret['events'][] = array(
						$reserva->getidReserva(),
						$visita->getnombre()." ".$visita->getapellido()." * "." Reserva: ".$tipoReserva.$estac,
						php2JsTime($phpTimeI),
						php2JsTime($phpTimeS),
						0,
						0, //more than one day event
						0,//Recurring event
						$color, //5 azul, 1 rojo
						1, //editable
						"S/N",
						''//$attends
				);				//die();			}	}		//print_r($ret);
	return $ret;
}function listCalendar($day, $type,$perUser,$idc){  //echo $perUser;  $phpTime = js2PhpTime($day);  $reserva = null;   //$nivel = $usuario->getPrivilegio();  //echo $phpTime . "+" . $type;  switch($type){  	    case "month":      $st = mktime(0, 0, 0, date("m", $phpTime), 1, date("Y", $phpTime));      $et = mktime(0, 0, -1, date("m", $phpTime)+1, 1, date("Y", $phpTime));      $cnt = 50;      //echo "mes".$day;            if($perUser>5)      {      	$reserva = controlReserva::getReservasforDate(null,php2MySql($st),php2MySql($et));      }      else      {      	      	$reserva = controlReserva::getReservasforDate(null,php2MySql($st),php2MySql($et),$idc);      }            break;    case "week":      //suppose first day of a week is monday	  $monday  =  date("d", $phpTime) - date('N', $phpTime) + 1;      //echo date('N', $phpTime);      $st = mktime(0,0,0,date("m", $phpTime), $monday, date("Y", $phpTime));      $et = mktime(0,0,-1,date("m", $phpTime), $monday+7, date("Y", $phpTime));  	    	  if($perUser>5)      {      	$reserva = controlReserva::getReservasforDate(null,php2MySql($st),php2MySql($et));      }      else      {      	      	$reserva = controlReserva::getReservasforDate(null,php2MySql($st),php2MySql($et),$idc);      }      //$cnt = 2;      break;    case "day":      $st = mktime(0, 0, 0, date("m", $phpTime), date("d", $phpTime), date("Y", $phpTime));      $et = mktime(0, 0, -1, date("m", $phpTime), date("d", $phpTime)+1, date("Y", $phpTime));  	    	  if($perUser>5)      {      	$reserva = controlReserva::getReservasforDate(null,php2MySql($st),php2MySql($et));      }      else      {      	      	$reserva = controlReserva::getReservasforDate(null,php2MySql($st),php2MySql($et),$idc);      }      //$cnt = 5;      //echo "dia".$day;      break;  }  //echo $st . "--" . $et;  return listCalendarByRangeTst($reserva,$st,$et);  //return listCalendarByRange($st, $et, $cnt);}function updateCalendar($id, $st, $et) //arrastrar y soltar en otros dias{	$ret = array();	$dataR = null;	$bandera = false;	$mensaje = null;  /*   * idCliente,idOperador,idVisita,fechaEntrada,fechaSalida,tipoReserva,tipoFrecuencia,piso,oficina,estacionamientoAsignado,estadoValidacion,momentoValidacion,codigoReserva,patenteVehiculo   * controlReserva::updateReservaGlobal($dataR,$idr)   *    */	//echo $id."_".$st."_".$et;	//die();	if($id!=null && $st!=null && $et!=null)	{		$dataR = array(null,null,null,$st,$et,null,null,null,null,null,null,null,null,null);		if(controlReserva::updateReservaGlobal($dataR,$id)>=0)		{			$mensaje = "Se actualizo el dia y la hora de la reserva exitosamente";			$bandera = true;		}		else 		{			$mensaje = "Intente esta operacion mas tarde";		}	}	else	{		$mensaje = "Intente esta operacion mas tarde";	}	        $ret['IsSuccess'] = $bandera;      $ret['Msg'] = $bandera;    return $ret;}function updateDetailedCalendar($idr,$idv, $idc, $st, $et, $nombre, $apellido, $mail, $pisos, $oficina, $patente, $esta){	  	  $ret = array();	  /* editaVisita(dato[])	   * 	   * UpdateVisita :   rut = ?,dv = ?,pasaporte = ?,nombre =?,apellido = ?, contacto = ?,telefono= ?,empresa = ?,rubro = ?,direccion = ? WHERE idVisita = ?	   * 	   * updateReservaGlobal($data,$idReserva)	   * 	   * update reserva: $dato[]	   * idCliente,idOperador,idVisita,fechaEntrada,fechaSalida,tipoReserva,tipoFrecuencia,piso,oficina,estacionamientoAsignado,estadoValidacion,momentoValidacion,codigoReserva,patenteVehiculo	   * 	   */	  //controlReserva::updateReservaGlobal($data,$idReserva);	  //echo $idv."_".$idc."_".$st."_".$et."_".$nombre."_".$apellido."_".$mail."_".$pisos."_".$oficina."_".$patente."_".$esta;	  $dataV = null;	  $dataR = null;	  $bandera = false;	  $tipoReserva = "Peatonal";	  $estadoValidaci贸n= "Reservada";	  $tipoFrecuencia = 0;	  $mensaje = null;	 // die();	  if($idv!=null && $idc!=null && $st!=null && $et!=null)	  {	  	if($patente==