	  			$bandera = false;	  		}	  	}	  	else 	  	{	  		$dataV = array(null,null,null,$nombre,$apellido,$mail,null,null,null,null,$idv);
	  		controlVisita::editaVisita($dataV);	  		//echo controlVisita::editaVisita($dataV);
	  		$dataR = array($idc,null,$idv,$st,$et,$tipoReserva,$tipoFrecuencia,$pisos,$oficina,$esta,$estadoValidaci贸n,null,$idv,$patente);	  		controlReserva::updateReservaGlobal($dataR,$idr);	  		$mensaje = "La reserva peatonal se actualizo exitosamente";	  		$bandera = true;	  	} 		  }	  else 	  {	  	$mensaje = "Porfavor intente mas tarde";	  	$bandera = false;	  }	  //$reserva = controlReserva::updateReservaGlobal($id);        $ret['IsSuccess'] = $bandera;      $ret['Msg'] = $mensaje;                    return $ret;}function removeCalendar($id){  $ret = array();  //eliminarRegistro($idReserva) solo reserva esporadica  //eliminarFrecuenciaDias($idReserva,$tipoF=null,$tipoNow=null) Si es frecuente elimina los dias tambien asocidaso a esa visita  $bandera = false;  $mensaje = null;  if($id!=null)  {  	controlReserva::eliminarFrecuenciaDias($id);  	controlReserva::eliminarRegistro($id);  	$bandera= true;  	$mensaje = "La reserva se elimino correctamente";  	  }  else  {  	$mensaje = "Problemas con la operaci贸n efectuada";  }      $ret['IsSuccess'] = $bandera;      $ret['Msg'] = $mensaje;  return $ret;}header('Content-type:text/javascript;charset=UTF-8');$method = $_GET["method"];switch ($method) {    case "add":        //$ret = addCalendar($_POST["CalendarStartTime"], $_POST["CalendarEndTime"], $_POST["Calenda<rTitle"], $_POST["IsAllDayEvent"]); //agregar al calendario        //print_r($_GET);        //print_r($_POST);        break;    case "list":    	
